///////////////////////////////////
// Function to slide out events  //
//  Grund: Khyber                //
///////////////////////////////////
function loadSideWithAjax(sUrl) {
    $.ajax({
        url: sUrl,
        type: "get",
        dataType: "html",
        // Data from the database prints out in a div with the id event_wrapper this.attrs.id.
        success: function (resp) {
            $("#event_wrapper").empty()
                .hide()
                .html(resp);
            // Toggle Options
            // 1. Desired Effect
            // 2. What Direction
            // 3. Duration in millisecs.
            $('#event_wrapper').show('slide', { direction : 'left', speed : 400 });
        }
    });
}
function loadMultipleShows(urlArr) {
    var test = "";
    $("#event_wrapper").empty().hide();
    urlArr.forEach(function(url) {
        $.ajax({
            type: "get",
            url: url,
            dataType: "html",
            success: function(data) {
                $("#event_wrapper").append(data);
                test += data;
            }
        });
    });
    $('#event_wrapper').show('slide', { direction : 'left', speed : 400 });
}

/////////////////////////////////////////////////////////
// Runs everytime Ajax has finished to bind clickables //
//  Base: Alfred                                       //
/////////////////////////////////////////////////////////

$(document).ajaxStop(function () {

// Reassures that the buttons functions doesn't run twice when pressed
    $('button.unbind_click').unbind('click');

    $('button[data-deletefile]').click(function () {
        var r = confirm("Are you sure?");
        if (r == true) {
            $(this).parent().hide();
            $.ajax({
                url: $(this).data('deletefile'),
                type: 'get',
                dataType: 'html',

                success: function (resp) {
                    $("#delete_file").hide();
                }
            });
        }
    });
// Sets the current in high_lights hidden field
    var c_year = new Date().getFullYear();
    $("#high_light_year").val(c_year);

    var year = 2013;
    var week = $("#event_datetime").data('week') + 1;
    var d = new Date("Dec 30, " + year + " 01:00:00");
    var w = d.getTime() + 604800000 * (week - 1);
    var n1 = new Date(w);
    var n2 = new Date(w + 518400000);

// Adds the calenderview input to the form
// minDate and maxDate sets the restrictions for ea week. Will be changed to working variables.
    $(function () {
        $("#event_datetime").datepicker({
            showWeek: true,
            firstDay: 1,
            minDate: n1,
            maxDate: n2,
            showOtherMonths: true,
            selectOtherMonths: true,
            dateFormat: 'yy-mm-dd'
        });
    });

    $('#output').colorPicker({
        defaultColor: $('#pre_color').val(),
        click: function(color){$('#event_dot_color').val(color);}
    });

    $('button[data-editevent]').click(function () {
        loadSideWithAjax($(this).data('editevent'));
    });
});
var Dot = function(posX, posY, thisLayer, weekNumber, userType, dot_color) {

    var rot = -90;
    var return_arr = [];
    var rad = 6; //Event dot radius

    for (var i = 0; i < dot_color.length; i++) {
        this.dot;
        // Starting point of drawing

        // //creates the letters which you can create a color from in hex
        // var letters = '0123456789ABCDEF'.split('');
        // var color = '#';

        // var parent = this;
        // // This loop then makes a random color.
        // for (var u = 0; u < 6; u++ ) {
        //     color += letters[Math.round(Math.random() * 15)];
        // }

        if(userType == "principal"){
            rad = 10;
        }

        if (dot_color.length == 1) {
            this.dot = new Kinetic.Shape({
                x: posX,
                y: posY,
                radius: rad,
                sceneFunc: function(context) {
                    context.beginPath();
                    context.arc(0,0, this.getAttrs().radius, 0, 360, false);
                    context.fillStrokeShape(this);
                },

                fill: dot_color[i],
                opacity: 1,
                stroke: '#444',
                strokeWidth: 0.8,
                id: 1,
                name: 'title'
            });
        } else {
            this.dot = new Kinetic.Wedge({
                x: posX,
                y: posY,
                radius: rad,
                angle: 360 / dot_color.length,
                fill: dot_color[i],
                rotation: rot,
                stroke: '#444',
                strokeWidth: 0.8,
                id: 1,
                name: 'title'
            });
        }

        this.dot.on("click", function(){
            // FIND ANIMATION IN wheel_animation.js
            selected_week = weekNumber;
            if (thisLayer == Layers.principal) {
                if (gon.current_schools.length /* > 1 */) {
                    var arr = [];
                    gon.current_schools.forEach( function(school) {
                        arr.push("sessions/show_week_events/" +weekNumber + "/" + school.id);
                    });
                    animation(arr);
                } else {
                    animation("sessions/show_week_events/" + weekNumber + "/" + userType);
                }
            } else {
                animation("sessions/show_week_events/" + weekNumber + "/" + userType);
            }
        });
        this.dot.on('mouseover', function () {
            document.body.style.cursor = 'pointer';
        });
        this.dot.on('mouseout', function () {
            document.body.style.cursor = 'default';
        });

        thisLayer.add(this.dot);

        rot += 360 / dot_color.length;
        return_arr.push(this.dot);
    }
    return return_arr;
}
;

