var Ring = function (name, innerBound, outerBound, color, thisLayer) {
    this.name = name;
    this.innerBound = innerBound;
    this.outerBound = outerBound;
    this.color = color;
    this.startingAngle = -90;
    this.wedgesCount = weekcount;
    this.wedgeWidth = 360 / this.wedgesCount;
    this.wedges = [];

    var parent = this;

    this.startingPoint = this.startingAngle - this.wedgeWidth;

    this.getWedgeById = function(wId) {
        var r;
        parent.wedges.forEach( function(wedge) {
            if (wId === wedge.getId()){
                r = wedge;
            }
        });
        return r;
    };
    for (var i = 0; i < this.wedgesCount; i++) {
        var wedge = new Kinetic.Shape({
            startingPoint: parent.startingPoint += parent.wedgeWidth,
            stoppingPoint: parent.startingPoint + parent.wedgeWidth,
            sceneFunc: function (context) {

                context.beginPath();
                var start = radians(this.getAttrs().startingPoint), stop = radians(this.getAttrs().stoppingPoint);

                // Outer arc
                context.arc(stage.getWidth()/2, stage.getHeight()/2, parent.innerBound, start, stop, false);

                // Inner arc
                context.arc(stage.getWidth()/2, stage.getHeight()/2, parent.outerBound, stop, start, true);

                context.closePath();
                context.fillStrokeShape(this);

            },
            fill: parent.color,
            opacity: 0.8,
            stroke: '#444',
            strokeWidth: 0.5,
            id: i,
            getId: function (){
                return parseInt(this.id);
            },
            dot: null,
            eventCount: 0
        });
        wedge.setColor = function(newColor) {
            this.getAttrs().fill = newColor;
        };
        wedge.editEvent = function(eventColor, pre_color) {
            selected_event_color = false;
            var colors = [];
            if (this.getAttrs().dot == null) {

                this.getAttrs().dot = [];
            }
            for (var x = 0; x < this.getAttrs().dot.length; x++) {
                if (pre_color == this.getAttrs().dot[x].getAttrs().fill && selected_event_color == false) {
                   this.getAttrs().dot[x].getAttrs().fill = eventColor;
                   selected_event_color = true;
                }
                colors.push(this.getAttrs().dot[x].getAttrs().fill);
                this.getAttrs().dot[x].remove();
            }

            // If eventInfo isn't set, it gets the value 1
            var eventCount = colors.length;

            this.getAttrs().eventCount = eventCount;

            // Sets the angle
            var centerAngleOfWedge = this.getAttrs().stoppingPoint - ((this.getAttrs().stoppingPoint - this.getAttrs().startingPoint) / 2);

            // Gets the center of the ring
            var rad = parent.outerBound - ((parent.outerBound - parent.innerBound) / 2);

            // Get the position of the wedge
            var posX = cX + rad * Math.cos(radians(centerAngleOfWedge));
            var posY = cY + rad * Math.sin(radians(centerAngleOfWedge));
            this.getAttrs().dot = new Dot(posX, posY, thisLayer, this.getAttrs().id, parent.name, colors);
        };
        wedge.addEvent = function(eventColor) {

            var colors = [];
            if (this.getAttrs().dot == null) {

                this.getAttrs().dot = [];
            }
            for (var x = 0; x < this.getAttrs().dot.length; x++) {
                colors.push(this.getAttrs().dot[x].getAttrs().fill);
                this.getAttrs().dot[x].remove();
            }
            for (var x = 0; x < eventColor.length; x++) {
                colors.push(eventColor[x]);
            }

            // If eventInfo isn't set, it gets the value 1
            var eventCount = colors.length;

            this.getAttrs().eventCount = eventCount;

            // Sets the angle
            var centerAngleOfWedge = this.getAttrs().stoppingPoint - ((this.getAttrs().stoppingPoint - this.getAttrs().startingPoint) / 2);

            // Gets the center of the ring
            var rad = parent.outerBound - ((parent.outerBound - parent.innerBound) / 2);

            // Get the position of the wedge
            var posX = cX + rad * Math.cos(radians(centerAngleOfWedge));
            var posY = cY + rad * Math.sin(radians(centerAngleOfWedge));
            this.getAttrs().dot = new Dot(posX, posY, thisLayer, this.getAttrs().id, parent.name, colors);
        };
        wedge.removeEvent = function(eventColor) {

            var colors = [];
            for (var x = 0; x < this.getAttrs().dot.length; x++) {
                colors.push(this.getAttrs().dot[x].getAttrs().fill);
                this.getAttrs().dot[x].remove();
            }
            for (var x = 0; x < eventColor.length; x++) {
                colors.splice( colors.indexOf( eventColor[x] ), 1 );
            }

            var newEventCount = colors.length;
            this.getAttrs().eventCount = newEventCount;
            if (newEventCount < 1) {
                this.getAttrs().dot.forEach( function(d) {
                    d.remove();
                })
                this.getAttrs().dot = null;
            } else {
                this.getAttrs().dot = new Dot(this.getAttrs().dot[0].getAttrs().x, this.getAttrs().dot[0].getAttrs().y, thisLayer, this.getAttrs().id, parent.name, colors);
            }
        };
        wedge.removeHighLight = function(){
            this.setColor(parent.color);
        }
        wedge.removeMark = function(){

            //this.setColor(parent.color);
            this.opacity(0.8);
        }
        wedge.on('click', function() {
            //code for marking starts here.
            var lastMarked = document.getElementById('mark').innerHTML;
            var indexOfslash = lastMarked.indexOf("/");
            var lastMarkedWeekId = lastMarked.substr(0,indexOfslash);
            var lastMarkedWeekUserType = lastMarked.substr(indexOfslash+1);
            lastMarkedWeekId = parseInt(lastMarkedWeekId);
            wheel.rings[lastMarkedWeekUserType].getWedgeById(lastMarkedWeekId).removeMark();
            Layers[lastMarkedWeekUserType].draw();
            document.getElementById('mark').innerHTML= this.getId()+"/"+parent.name;
            
            // code for marking week finishs here.
            var eId = this.getId();
            var eIdplus = parseInt(eId);
            selected_week = this.getId();
            selected_ring = parent.name;
            if (this.getAttrs().dot != null && ($.isArray(this.getAttrs().dot) && this.getAttrs().dot.length > 0)) {
                var dots = this.getAttrs().dot[0];
                console.log(dots.fire('click'));
            } else {

                if (parent.name == gon.c_user_type || (gon.c_user_type == 'admin' && parent.name == 'juror')) {
                    if (parent.name == 'teacher'){
                        if (this.attrs.fill == "red" || this.attrs.fill == "yellow" || this.attrs.fill == "green") {
                            // FIND ANIMATION IN wheel_animation.js
                            animation("sessions/show_high_lights/"+eId);
                            this.opacity(0.5);
                            thisLayer.draw();
                        } else {
                            animation("sessions/new_high_lights/"+eIdplus);
                            this.opacity(0.5);
                            thisLayer.draw();
                        }
                    } else if (gon.c_user_type == 'admin' && parent.name == 'juror') {
                        this.opacity(0.5);
                        thisLayer.draw();
                        animation("sessions/new_week_event/"+eIdplus+"/juror");
                    } else {
                        this.opacity(0.5);
                        thisLayer.draw();
                        animation("sessions/new_week_event/"+eIdplus);
                    }
                }
            }
        });
        wedge.on('mouseover', function () {
            if (parent.name == gon.c_user_type || (gon.c_user_type == 'admin' && parent.name == 'juror')) {
                document.body.style.cursor = 'pointer';
            }
        });
        wedge.on('mouseout', function () {
            document.body.style.cursor = 'default';
        });

        parent.wedges.push(wedge);
        thisLayer.add(wedge);
    }
};
   //////////////////////////////////
  // Class to draw the wheel      //
 //  Grund: Shining              //
//////////////////////////////////
var Wheel = function (cX, cY) {
    //Get center and out radius of wheel
    var centerX = cX;
    var centerY = cY;
    var radius = 300;
    var pictureRadius = 120;
    var selected_week;


    this.rings = {};

    var today = new Date();
    var thisYear = today.getFullYear();

    // Gets the daycount of all the months, with the amount of
    // days from december last year in this year as the first ex:
    // [2, 31, 28, 31...
    var thisYearMonthDayCount = getMonthDayCount(thisYear);

    var dayWidth = 360 / 365;
    var startingAngle = 0;
    var endingAngle = 0;
    var color = "#ccc";

    thisYearMonthDayCount.forEach( function(monthCount) {
        startingAngle = endingAngle;
        endingAngle = endingAngle + dayWidth * monthCount;

        color == "#ccc" ? color = "#fff" : color = "#ccc";

        backRing(startingAngle, endingAngle, color);
    });

    for (var i = 0; i < weekcount+1; i++) {

        // Set angle
        var angle = (360 / 52) * i;

        var theta = radians(angle);

        var xLineStart = centerX + radius * Math.cos(theta);
        var yLineStart = centerY + radius * Math.sin(theta);
        var xLineEnd = centerX + pictureRadius * Math.cos(theta);
        var yLineEnd = centerY + pictureRadius * Math.sin(theta);

        linemaker(xLineStart, yLineStart, xLineEnd, yLineEnd);

    }

    this.rings['admin'] = new Ring('admin', 265, 280, '#FBBE5B', Layers.admin);
    this.rings['juror'] = new Ring('juror', 235, 250, '#F075AE', Layers.juror);
    this.rings['principal'] = new Ring('principal', 190, 220, '#56ADBA', Layers.principal);
    this.rings['teacher'] = new Ring('teacher', 135, 175, '#7AB292', Layers.teacher);

}








;

//Function for getting the radians on an angle
function radians(degrees) {
    return (Math.PI / 180) * degrees;
}

// Returns an array with the amount of days in every month
// and the count of days of december last year in week 1 as
// the first value in the array
function getMonthDayCount(year){
    var dayCounts = [];
    var firstDateOfTheYear = new Date(year, 0, 0);
    var numberOfWeekDay = firstDateOfTheYear.getDay();
    dayCounts.push(numberOfWeekDay);

    // Pushes the day-count of all the months into the array
    for (var i = 1; i <= 12; i++) {
        dayCounts.push(new Date(year, i, 0).getDate());
    }

    return dayCounts;
}
